mapper.select:
select * from question where stsCd='A'
and visitDate between #{startDate} and #{endDate}
order by visitDate

select.if:
select sum(accompanyNumber) as value from question
where visitDate between #{startDate} and #{endDate}
group by NULL

select count(1) as value
from question

    where visitDate between #{startDate} and #{endDate}

select dictName as name,IFNULL(table1.value,0) as value from dict left join(select ${field} as name ,count(1) as value from question
where visitDate between #{startDate} and #{endDate}
group by ${field}) as table1 on dict.code=table1.name where dictType=#{dictType}
